 if ( ! defined( 'ABSPATH' ) ) { exit; } class FS_Plugin extends FS_Scope_Entity { public $parent_plugin_id; public $title; public $slug; public $premium_slug; public $type; public $affiliate_moderation; public $is_wp_org_compliant = true; public $premium_releases_count; public $file; public $version; public $auto_update; public $info; public $is_premium; public $premium_suffix; public $is_live; public $bundle_id; public $bundle_public_key; public $opt_in_moderation; const AFFILIATE_MODERATION_CUSTOMERS = 'customers'; function __construct( $plugin = false ) { parent::__construct( $plugin ); $this->is_premium = false; $this->is_live = true; if ( empty( $this->premium_slug ) && ! empty( $plugin->slug ) ) { $this->premium_slug = "{$this->slug}-premium"; } if ( empty( $this->premium_suffix ) ) { $this->premium_suffix = '(Premium)'; } if ( isset( $plugin->info ) && is_object( $plugin->info ) ) { $this->info = new FS_Plugin_Info( $plugin->info ); } } function is_addon() { return isset( $this->parent_plugin_id ) && is_numeric( $this->parent_plugin_id ); } function has_affiliate_program() { return ( ! empty( $this->affiliate_moderation ) ); } static function get_type() { return 'plugin'; } }