<?php

if ( ! defined( 'ABSPATH' ) ) { exit; }

 if ( ! defined( 'ABSPATH' ) ) { exit; } class FS_Pricing extends FS_Entity { public $plan_id; public $licenses; public $monthly_price; public $annual_price; public $lifetime_price; public $currency; function __construct( $pricing = false ) { parent::__construct( $pricing ); } static function get_type() { return 'pricing'; } function has_monthly() { return ( is_numeric( $this->monthly_price ) && $this->monthly_price > 0 ); } function has_annual() { return ( is_numeric( $this->annual_price ) && $this->annual_price > 0 ); } function has_lifetime() { return ( is_numeric( $this->lifetime_price ) && $this->lifetime_price > 0 ); } function is_unlimited() { return is_null( $this->licenses ); } function is_multi_cycle() { $cycles = 0; if ( $this->has_monthly() ) { $cycles ++; } if ( $this->has_annual() ) { $cycles ++; } if ( $this->has_lifetime() ) { $cycles ++; } return $cycles > 1; } function annual_discount_percentage() { return floor( $this->annual_savings() / ( $this->monthly_price * 12 * ( $this->is_unlimited() ? 1 : $this->licenses ) ) * 100 ); } function annual_savings() { return ( $this->monthly_price * 12 - $this->annual_price ) * ( $this->is_unlimited() ? 1 : $this->licenses ); } function is_usd() { return ( 'usd' === $this->currency ); } }