<?php//--------------- Adding Latest jQuery------------//function wpb_bls_jquery() {	wp_enqueue_script('jquery');}add_action('init', 'wpb_bls_jquery');//-------------- include js files---------------//function wpb_bls_adding_scripts() {	if (!is_admin()) {			wp_register_script('owl-carousel', plugins_url('assets/js/owl.carousel.min.js', __FILE__),array('jquery'),'1.0', true);				wp_enqueue_script('owl-carousel');	}}add_action( 'wp_enqueue_scripts', 'wpb_bls_adding_scripts' ); //------------ include css files-----------------//function wpb_bls_adding_style() {	if (!is_admin()) {		wp_register_style('owl-carousel-style', plugins_url('assets/css/owl.carousel.css', __FILE__),'','1.0', false);		wp_register_style('main-style', plugins_url('assets/css/main.css', __FILE__),'','1.0', false);				wp_enqueue_style('owl-carousel-style');		wp_enqueue_style('main-style');	}}add_action( 'init', 'wpb_bls_adding_style' );