<input	type="checkbox"	id="dummy_<?php _e($field_data['key']) ?>"	name=""	value="<?php _e($saved_value) ?>"	<?php _e($saved_value == PostMetaManagerHelper::CheckboxChecked ? 'checked="checked"' : '') ?> 	<?php _e(isset($disabled) && $disabled === true ? 'disabled="disabled"' : '') ?> /><input	type="checkbox"	id="<?php _e($field_data['key']) ?>"	name="<?php _e($identifier) ?>"	value="<?php _e($saved_value) ?>"	<?php _e($saved_value == PostMetaManagerHelper::CheckboxChecked ? 'checked="checked"' : '') ?> 	style="display: none;"/><?php if (isset($label) && !empty($label)) { ?>	&nbsp;&nbsp;&nbsp;<label for="dummy_<?php _e($field_data['key']) ?>"><?php _e($label) ?></label><?php } ?><script type="text/javascript">	jQuery(function ($)	{		var $checkbox = $("#<?php _e($field_data['key']) ?>");		var $dummyCheckbox = $("#dummy_<?php _e($field_data['key']) ?>");		$dummyCheckbox.change(function ()		{			var $this = $(this);			if ($this.is(":checked"))			{				$checkbox.val("<?php _e(PostMetaManagerHelper::CheckboxChecked) ?>");				$checkbox.attr("checked", "checked");			}			else			{				$checkbox.val("<?php _e(PostMetaManagerHelper::CheckboxUnchecked) ?>");				$checkbox.attr("checked", "checked");			}		});	});</script>