<?php	$default_value = isset($default_value) && !empty($default_value) ? $default_value : '';	$change        = isset($change)        && !empty($change)        ? $change        : '';		$saved_value = PostMetaManagerHelper::esc_attr($saved_value);	if (empty($saved_value) && !empty($default_value)) $saved_value = $default_value;?><input	type="text"	name="<?php _e($identifier) ?>"	id="<?php _e($identifier) ?>"	value="<?php _e($saved_value) ?>"	style="width: 100%; max-width: 100px; <?php _e(isset($style) && !empty($style) ? $style : '') ?>"/><script type="text/javascript">	jQuery(function ($)	{		$("#<?php _e(PostMetaManagerHelper::sanitize_css_selector($identifier)) ?>").wpColorPicker(<?php _e(!empty($change) ? '{change: ' . $change . '}' : '') ?>);	});</script>