function  save_form(){
	jQuery('#save_all').attr('disabled','disabled');
	var btn_bs_inset_val= (jQuery('#btn_bs_inset').is(':checked')==true)? 'inset':'';
	jQuery.post(ajaxurl,
		{	action : 'myajax_hndl',
			myaction : 'save_cf7cs_form',
			data:jQuery('#setting_form').serializeArray(),
			btn_bs_inset:btn_bs_inset_val,
			wpcf7_id:jQuery('#wpcf7_id').val()
		}, function(data) {
			jQuery('#shrtcd_dv').html('Shortcode - [custom-skins-contact-form-7 id="'+data+'"]');
			jQuery('#cf7cs_id').val(data);
			jQuery('#msg_sv').attr('style','dispaly:block;');
			jQuery('#msg_sv').html('<img src="'+jQuery('#path').val()+'images/done.png"/>  Setting Saved');
			jQuery('#msg_sv').delay(3000).fadeOut(3000);
			jQuery('#save_all').removeAttr('disabled');
		});			
}

function load_form(form_id){
	if(form_id!='' ){
		jQuery('#form_display').html('Please Select Form');
		jQuery('#out_form').attr('style','');
		jQuery('#footer_section').html('');
		jQuery('#header_section').html('');
		jQuery('#footer_section').attr('style','');
		jQuery('#header_section').attr('style','');
		jQuery.ajax({
			type: "POST",
			url: ajaxurl,
			data:{form_id : form_id,action:'myajax_hndl',myaction : 'display_wpcf7_form'},
			 dataType: "html"
		})
		 .done( function(data) {
		 	jQuery('#mn_cntinr').show();
		 	jQuery('#form_display').html(data);
		});		
	}else{
		jQuery('#mn_cntinr').hide();
	 	jQuery('#form_display').html('Please Select Form');
		jQuery('#shrtcd_dv').html('');
		change_css();

	}
	return false;
}
