    /**
     * Init the Builder.
     *
     * @since 6.0
     */
    public function init()
    {
        if (!is_admin()) {
            return;
        }
        // register admin menu for sby-feed-builder
        add_action('admin_menu', array($this, 'register_menu'), 99);
        // add ajax listeners
        $this->saver_manager->hooks();
        \Smashballoon\Customizer\Source::hooks();
        self::hooks();
    }
