    /**
     * Get WP Pages List
     *
     * @return array
     *
     * @since 6.0
     */
    public function get_wp_pages()
    {
        $pagesList = get_pages();
        $pagesResult = array();
        if (\is_array($pagesList)) {
            foreach ($pagesList as $page) {
                \array_push($pagesResult, array('id' => $page->ID, 'title' => $page->post_title));
            }
        }
        return $pagesResult;
    }
