    /**
     * Get dynamic heading for the extension popup depending on license state
     * 
     * @since 2.0.3
     */
    public function get_extension_popup_dynamic_heading($extension_title, $license_tier)
    {
        $license_tier_level = $license_tier->get_license_tier();
        $title = '';
        if (sby_license_inactive_state()) {
            $title = \sprintf(__('Activate your License to get %s', 'feeds-for-youtube'), $extension_title);
        } else {
            if (sby_license_notices_active()) {
                $title = \sprintf(__('Renew license to get %s', 'feeds-for-youtube'), $extension_title);
            } else {
                if ($license_tier_level == 'personal') {
                    $title = \sprintf(__('Upgrade to Developer to get %s', 'feeds-for-youtube'), $extension_title);
                } else {
                    if ($license_tier_level == 'business') {
                        $title = \sprintf(__('Upgrade to Developer </br> to get %s', 'feeds-for-youtube'), $extension_title);
                    }
                }
            }
        }
        return $title;
    }
    /**
     * Get dynamic upgrade/activate/renew URL depending on license state
     * 
     * @since 2.0.3
     */
    public function get_extension_popup_dynamic_buy_url($license_key)
    {
        if (empty(Util::get_license_key())) {
            return admin_url('admin.php?page=youtube-feed-settings');
        }
        if (!empty(Util::get_license_key()) && Util::is_license_expired()) {
            return $this->license_service->get_renew_url();
        }
        return \sprintf('https://smashballoon.com/pricing/youtube-feed/?license_key=%s&upgrade=true&utm_campaign=youtube-free&utm_source=feed-type&utm_medium=youtube-feed&utm_content=upgrade', $license_key);
    }
