jQuery(document).ready(function($){gfpe_uploader={uploader:function(widget_id,widget_id_string ){var frame=wp.media({title:gfpe_media.frame_title,multiple:false,library:{type:'image'},button:{text:gfpe_media.button_title}});frame.on('close',function(){var attachments=frame.state().get('selection').toJSON();gfpe_uploader.render(widget_id,widget_id_string,attachments[0] );});frame.open();return false;},render:function(widget_id,widget_id_string,attachment ){$("#"+widget_id_string+'image_url').val(attachment.url);},updateInputIfEmpty:function(widget_id_string,name,value ){var field=$("#"+widget_id_string+name);if(field.val()=='' ){field.val(value);}},};});