custom_css.post_content:
#home-search .entry-content .hotel-booking-search button {
  width: initial;
}

#home-search .hb-submit {
	display:inline-block;
}

#home-search .hb-form-table {
	display:inline-block;
}

#home-search .hb-form-field {
	display:inline-block;
}

#home-search .hb-form-field-input {
	display:block;
}

#home-search .hotel-booking-search button {
	background-color: #eab96a;
	border-color:white;
}

#home-search .hotel-booking-search .sort-by-wrapper {
	display:none;
}

#home-search .hotel-booking-search form {
	text-align:center;
}

#home-search .hotel-booking-search select {
	background-color:white;
}

#home-search .hotel-booking-search {
  width: initial;
  padding: 15px;
  margin-top: -25px;
}
#home-search .hotel-booking-search h3 {
	display:none;
}

#home-search .hotel-booking-search input {
	background-color:white;
}

#home-search .hotel-booking-search label {
	color:white;
	font-weight: 600;
}


.site-footer .footer-top .grid .col section .textwidget p {
	color: var(--footer-text-color);
}

.site-footer .social-links li a svg {
  fill: var(--footer-text-color);
}

div.wpforms-container-full button[type="submit"]:not(:hover):not(:active) {
  background-color: var(--gbl-primary-color);
}

