<?php

// DO NOT EDIT THIS FILE. Go to ./config/social.php and fill in API credentials

function twl_pip_config() {
	
$config = array();

$config['facebook'] = $config['twitter'] = array();

// path to facebook SDK and templates used to display feed grid ** DO NOT EDIT THESE **
$config['facebook']['sdk'] = TWL_PIP_SDKPATH . '/fb/facebook.php';
$config['facebook']['item_template'] = TWL_PIP_TEMPLATES . '/fb-feed-item.php';
$config['facebook']['container_template'] = TWL_PIP_TEMPLATES . '/fb-feed-container.php';

// path to twitter SDK and templates used to display feed grid ** DO NOT EDIT THESE **
$config['twitter']['sdk'] = TWL_PIP_SDKPATH . '/twitter/codebird.php';
$config['twitter']['item_template'] = TWL_PIP_TEMPLATES . '/twitter-feed-item.php';
$config['twitter']['container_template'] = TWL_PIP_TEMPLATES . '/twitter-feed-container.php';

// what should be used to load classes ?
// If your server and WP installation supports php autoloader, use TWL_PIP_CLASSPATH . '/autoloader.php';
// else use TWL_PIP_CLASSPATH . '/classes.php'
$config['class_loader'] = TWL_PIP_CLASSPATH . '/classes.php';

// list of all css files needed by the plugin
$config['plugin_css'] =  array(
	plugins_url('inc/style.css', __FILE__),
);

// list of all javascript files needed by the plugin
$config['plugin_js'] = array(
	plugins_url('inc/app.js', __FILE__),
);

return $config; 


}