<div id="plsbvs-popup-box-preview-{{num}}" class="popupally-outer-preview-plsbvs-{{num}} popupally-outer-preview-background-plsbvs-{{num}}">
	<div class="popupally-inner-plsbvs-{{num}}">
		<div class="popupally-center-plsbvs">
			<div popupally-preview-linked="plsbvs-config-headline-{{num}}" class="desc-plsbvs plsbvs-customizable-color-text-{{num}} plsbvs-preview-headline-{{num}}" id="plsbvs-preview-headline-{{num}}"></div>
			<div class="content-plsbvs">
				<input popupally-preview-linked="plsbvs-config-name-input-{{num}}" type="text" id="plsbvs-preview-name-{{num}}" name="name" class="field-plsbvs-name plsbvs-preview-name-{{num}}" placeholder="{{plsbvs-name-placeholder}}"/>
				<input popupally-preview-linked="plsbvs-config-email-input-{{num}}" type="email" id="plsbvs-preview-email-{{num}}" name="email" class="field-plsbvs-email plsbvs-preview-email-{{num}}" placeholder="{{plsbvs-email-placeholder}}"/>
				<input popupally-preview-linked="plsbvs-config-subscribe-button-{{num}}" type="button" id="plsbvs-subscribe-button-{{num}}" class="submit-plsbvs no-click-through plsbvs-subscribe-button-{{num}}" value="{{plsbvs-subscribe-button-text}}"/>
			</div>
		</div>
	</div>
</div>