custom_css:
.trial_toolbar{position:relative;top:0;left:0;width:100%;background:#004274;z-index:1000;height:55px;vertical-align:middle;line-height:55px}
.trial_toolbar>div{display:inline-block}
.trial_toolbar .realtyna_logo{width:95px;margin-left:20px}
@media (max-width: 768px){.trial_toolbar .realtyna_logo{margin-left:5px}}
@media (max-width: 480px){.trial_toolbar .realtyna_logo{width:80px}}
.trial_toolbar .realtyna_logo img{vertical-align:middle;height:45px;}
.trial_toolbar .trial_contacts{text-align:right;float:right;width:calc(100% - 130px)}
@media (max-width: 480px){.trial_toolbar .trial_contacts{width:calc(100% - 85px)}}
.trial_toolbar .trial_contacts>div{display:inline-block}
.trial_toolbar .trial_phone a{color:white;font-size:18px;}
@media (max-width: 480px){.trial_toolbar .trial_phone a{font-size:12px}}
.trial_toolbar .trial_phone a:before{content:'\f095';display:inline-block;line-height:normal;vertical-align:middle;padding-right:5px;font-family:'FontAwesome';font-size:22px;}
.trial_toolbar .trial_phone a:hover{color:#bbb}.trial_toolbar .trial_contacts a{text-decoration:none;padding:0 30px;transition:300ms}@media (max-width: 480px){.trial_toolbar .trial_contacts a{padding:0 4px}}.trial_toolbar .trial_form{vertical-align:top;margin-right:-15px}.trial_toolbar .trial_form a{text-transform:uppercase;color:#1a1a1a !important;background:url("http://wpl28.realtyna.com/theme28/wp-content/themes/wpl_twentyeight-child/assets/images/trial_button.png") no-repeat center/cover;height:55px;display:block ;font-size:18px}@media (max-width: 480px){.trial_toolbar .trial_form a{font-size:12px;padding-right:20px}}.trial_toolbar .trial_form a span{font-weight:900;color:#1a1a1a !important;}.trial_toolbar .trial_form a:hover{box-shadow:inset 1px 2px 6px #c17d35}.html_header_top.html_header_sticky #main {padding-top: 145px;}
.buy_theme,.wpl_shop{transition:.3s;border-radius:5px;line-height:38px;margin-right:15px;}
.trial_toolbar .buy_theme a,.trial_toolbar .wpl_shop a{color:#fff;padding:0 15px;line-height: 38px;display: block;}
.buy_theme{background:#999;}.wpl_shop{background:#19bc9c;}
.buy_theme:hover,.wpl_shop:hover{opacity: .9;}

custom_js_header:
jQuery(document).ready(function(){
    var toolbar = jQuery('.trial_toolbar');
    jQuery('.trial_toolbar').remove();
    jQuery(toolbar).prependTo('.re-header');
});
