add_action( 'init', 'secupress_ssl_fix_load_plugin_textdomain_translations', 0 );
function secupress_ssl_fix_load_plugin_textdomain_translations() {
	load_plugin_textdomain( 'secupress-ssl-fixer', false, dirname( plugin_basename( __FILE__ ) ) . '/languages' );
}

add_filter( 'load_textdomain_mofile', 'secupress_ssl_fix_load_own_i18n', 10, 2 );
function secupress_ssl_fix_load_own_i18n( $mofile, $domain ) {
	if ( 'secupress_ssl_fix' === $domain && false !== strpos( $mofile, WP_LANG_DIR . '/plugins/' ) ) {
		$locale = apply_filters( 'plugin_locale', determine_locale(), $domain );
		if ( ! function_exists( 'determine_locale' ) ) { // WP 5.0.
			$determined_locale     = get_locale();
			if ( is_admin() ) {
				$determined_locale = get_user_locale();
			}
		} else {
			$determined_locale = determine_locale();
		}
		$locale = apply_filters( 'plugin_locale', $determined_locale, $domain );
		if ( 'fr_CA' === $locale || 'fr_BE' === $locale ) {
			$locale = 'fr_FR';
		}
		$mofile = WP_PLUGIN_DIR . '/' . dirname( plugin_basename( __FILE__ ) ) . '/languages/' . $domain . '-' . $locale . '.mo';
	// wp_die( $mofile );
	}
	return $mofile;
}

add_action( 'init', 'secupress_ssl_fix_it_now' );
function secupress_ssl_fix_it_now() {
	if ( get_transient( 'secupress-ssl-fixer' ) ) {
		add_filter( 'https_ssl_verify', '__return_false' );
	} else {
		update_option( 'secupress_ssl_fix_settings', [] );
	}
}

register_deactivation_hook( __FILE__, 'secupress_ssl_fix_plugin_deactivate' );
function secupress_ssl_fix_plugin_deactivate() {
	delete_option( 'secupress_ssl_fix_settings' );
	delete_transient( 'secupress-ssl-fixer' );
}

add_action( 'admin_menu', 'secupress_add_admin_menu' );
function secupress_add_admin_menu(  ) { 
	add_options_page( 'SecuPress SSL Fixer', 'SecuPress SSL Fixer', 'manage_options', 'ssl_fixer', 'secupress_options_page' );
}

add_filter( 'plugin_action_links_' . plugin_basename( __FILE__ ), 'secupress_add_plugin_page_settings_link' );
function secupress_add_plugin_page_settings_link( $links ) {
	$links[] = '<a href="' . admin_url( 'options-general.php?page=ssl_fixer' ) . '">' . __( 'Settings' ) . '</a>';
	return $links;
}

add_action( 'admin_init', 'secupress_ssl_fix_settings_init' );
function secupress_ssl_fix_settings_init(  ) { 

	register_setting( 'pluginPage', 'secupress_ssl_fix_settings', 'secupress_ssl_fix_settings_cb' );

	add_settings_section(
		'secupress_pluginPage_section', 
		'', 
		'secupress_ssl_fix_settings_section_callback', 
		'pluginPage'
	);

	add_settings_field( 
		'secupress_select_field_0', 
		__( 'How much time?', 'secupress-ssl-fixer' ), 
		'secupress_select_field_0_render', 
		'pluginPage', 
		'secupress_pluginPage_section' 
	);

	add_settings_field( 
		'secupress_select_field_1', 
		__( 'Status', 'secupress-ssl-fixer' ), 
		'secupress_select_field_1_render', 
		'pluginPage', 
		'secupress_pluginPage_section' 
	);


}

function secupress_ssl_fix_settings_cb( $settings ) {
	if ( ! isset( $settings['secupress_select_field_0'] ) ) {
		return [ 'secupress_select_field_0' => 0 ];
	}
	switch ( $settings['secupress_select_field_0'] ) {
		case 0:
			delete_transient( 'secupress-ssl-fixer' );
		break;
		case 1:
			set_transient( 'secupress-ssl-fixer', time() + 5 * MINUTE_IN_SECONDS, 5 * MINUTE_IN_SECONDS );
		break;
		case 2:
			set_transient( 'secupress-ssl-fixer', time() + HOUR_IN_SECONDS, HOUR_IN_SECONDS );
		break;
		case 3:
			set_transient( 'secupress-ssl-fixer', time() + DAY_IN_SECONDS, DAY_IN_SECONDS );
		break;
	}
	return $settings;
}

function secupress_select_field_1_render(  ) { 
	if ( $tr = get_transient( 'secupress-ssl-fixer' ) ) {
		printf( __( '⛔ &nbsp;&nbsp;SSL Check is <b>DEACTIVATED</b>, %s left.', 'secupress-ssl-fixer' ), human_time_diff( time(), $tr ) );
	} else {
		if ( apply_filters( 'https_ssl_verify', '1' ) ) {
			_e( '✅ &nbsp;&nbsp;SSL Check is still <b>ACTIVATED</b>', 'secupress-ssl-fixer' );
		} else {
			_e( '⛔ &nbsp;&nbsp;SSL Check is <b>DEACTIVATED</b>, but not because of this plugin,<br>1/ check your <code>/mu-plugins/</code> folder,<br>2/ check your <code>functions.php</code> from theme,<br>3/ check your plugins,<br>4/ i’m as lost as you….', 'secupress-ssl-fixer' );
		}
	}
}

function secupress_select_field_0_render(  ) { 

	$options = get_option( 'secupress_ssl_fix_settings' );
	if ( ! isset( $options['secupress_select_field_0'] ) ) {
		$options['secupress_select_field_0'] = 0;
	}
	?>
	<select name='secupress_ssl_fix_settings[secupress_select_field_0]'>
		<option value="0"<?php selected( $options['secupress_select_field_0'], 0 ); ?>><?php _e( 'Do not deactivate SSL Check', 'secupress-ssl-fixer' ); ?></option>
		<option value="1" <?php selected( $options['secupress_select_field_0'], 1 ); ?>><?php _e( '5 minutes', 'secupress-ssl-fixer' ); ?></option>
		<option value="2" <?php selected( $options['secupress_select_field_0'], 2 ); ?>><?php _e( '1 hour', 'secupress-ssl-fixer' ); ?></option>
		<option value="3" <?php selected( $options['secupress_select_field_0'], 3 ); ?>><?php _e( '1 day', 'secupress-ssl-fixer' ); ?></option>
	</select>
	<p class="description"><?php _e( 'There is no "Always" option since this is not secure to deactivate the SSL Check, this is why this has to be done temporarily.', 'secupress-ssl-fixer' ); ?></p>
<?php

}


