<?phpinclude 'input.php';global $wpdb;if (isset($_GET['removeForm'])) {    if(wp_verify_nonce( $_REQUEST['_wpnonce'], 'icp-serp-852' )){        $wpdb->query("DELETE FROM " . $wpdb->prefix . "icp_sa_forms WHERE id = " . sanitize_text_field($_GET['removeForm']));    }        echo '<script>  window.location = "' . admin_url('admin.php?page=icp_site_auditor_pro') . '";  </script>';        die();}?><script>    function srpd_deleteForm() {        ask = confirm("Do you really want to delete this form?");        if (ask)            window.location = '<?php echo admin_url('admin.php?page=icp_site_auditor_pro&removeForm'); ?>';    }</script><div class="wrap">    <table width="100%" border="0" cellspacing="0" cellpadding="3">        <tr>            <td width="30"><img src="<?= srpd_root_path() ?>img/siteauditorpro.png" width="30" height="30" /></td>            <td width="210"><b style="font-size: 2em;">Site Auditor Pro</b></td>            <td><input type="button" name="button" id="button" class="button" style="height:100% !important" value="Add New Form" onclick="window.location = '<?php echo admin_url('admin.php?page=icp_site_auditor_pro_add'); ?>'" /></td>        </tr>    </table>    <div style="clear:both;"></div>    <?php    if (isset($_REQUEST['message'])) {        echo '<div id="message" class="' . $_REQUEST["type"] . '" ><p>' . $_REQUEST['message'] . '</p></div>';    }    ?>    <?php    $forms = $wpdb->get_results("SELECT * FROM " . $wpdb->prefix . "icp_sa_forms ORDER BY id DESC", ARRAY_A);    if (count($forms) == 0)        echo 'You haven\'t added any form yet. <a href="' . admin_url('admin.php?page=icp_site_auditor_pro_add') . '">Click here</a> to add a new form.';    else {        //get_projects        $get_projects = wp_remote_get("https://members.serped.net/plugin/plugin.site.auditor.php?plugin_key=" . srpd_get_plugin_key() . "&listProjects");        $get_projects = wp_remote_retrieve_body($get_projects);        $block = false;        if ($get_projects) { //check if request is completed            $get_projects = json_decode($get_projects, true);            if (isset($get_projects['status']) && ($get_projects['status'] == 'not_found' || $get_projects['status'] == 'no_projects')) { //if public key is not found                $block = true;                $get_projects = '<span style="color:red">no project found</span><br />This form project was not found in SERPed.net.';            }        } else {            $block = true;            $get_projects = 'n/a';        }        ?>        <table class="widefat fixed" cellspacing="0">            <thead>                <tr>                    <th id="cb" class="manage-column column-cb check-column" scope="col" style="width:10%; text-align:center">ID</th>                    <th id="cb" class="manage-column column-cb check-column" scope="col" style="width:30%;">Name</th>                    <th id="cb" class="manage-column column-cb check-column" scope="col" style="width:20%; text-align:center">Project</th>                    <th id="cb" class="manage-column column-cb check-column" scope="col" style="width:20%; text-align:center">Style</th>                    <th id="cb" class="manage-column column-cb check-column" scope="col" style="width:20%; text-align:center">ShortCode</th>                </tr>            </thead>            <tbody>                <?php                foreach ($forms as $frm) {                    ?>                    <tr class="alternate">                        <td class="column-columnname" style=" text-align:center">  <?= $frm['id'] ?>	  </td>                        <td class="column-columnname">                            <strong><?= $frm['icp_sa_name'] ?></strong>                            <div class="row-actions">                                <span><a href="<?php echo admin_url('admin.php?page=icp_site_auditor_pro_edit&id=' . $frm['id']); ?>">Edit</a> |</span>                                <span><a href="<?php echo admin_url('admin.php?page=icp_site_auditor_pro&removeForm=' . $frm['id'].'&_wpnonce='.wp_create_nonce('icp-serp-852')); ?>" style="color:#c00" class="delete_link_projects">Delete</a></span>                            </div>                        </td>                        <td class="column-columnname" style=" text-align:center">  <?php                            if ($block == true)                                echo $get_projects;                            else {                                foreach ($get_projects as $project) {                                    if ($project['apikey'] == $frm['icp_sa_pid']) {                                        echo '<strong>' . $project['name'] . '</strong>';                                        break;                                    }                                }                            }                            ?>	  </td>                        <td class="column-columnname" style=" text-align:center">  <?php                            if (strpos($frm['icp_optin_box_style'], 'slide') > -1)                                echo 'slide';                            else                                echo $frm['icp_optin_box_style'];                            ?>	  </td>                        <td class="column-columnname" style=" text-align:center">  <?php                            if ($frm['icp_optin_box_style'] == 'form')                                echo "[icp_sa_form id='" . $frm['id'] . "']";                            else                                echo '-';                            ?>   </td>                    </tr>                <?php } ?>            </tbody>        </table>    <?php } ?></div><script>    jQuery(".delete_link_projects").click( function( event ) {        if( ! confirm( "Are you sure you want to delete this Project?" ) ) {            event.preventDefault();        }               });</script>