<?php
// Exit if accessed directly
if (!defined('ABSPATH')) {
    exit;
}

// Show whatsapp chat button in frontend
function scb_show_whatsapp_button() {
    $post_id = get_the_ID();
    $button_status = intval(get_option('scb_button_status'));
    $button_hide_status = intval(get_post_meta($post_id, '_scb_button_hide_status', true));
    if ($button_status === 1 && $button_hide_status !== 1 && !is_admin()){
        // Initialize variables
        $whatsapp_number = get_option('scb_whatsapp_number');
        $whatsapp_chat_text = get_option('scb_whatsapp_chat_text');
        $button_text = get_option('scb_button_text');
        $button_target = get_option('scb_button_target');
		$button_position = get_option('scb_button_position');
        $button_z_index = intval(get_option('scb_button_z_index'));
        $desktop_link_type = get_option('scb_desktop_link_type');
        $desktop_bottom_margin = intval(get_option('scb_desktop_bottom_margin'));
        $tablet_bottom_margin = intval(get_option('scb_tablet_bottom_margin'));
        $mobile_bottom_margin = intval(get_option('scb_mobile_bottom_margin'));
        $whatsapp_chat_text = urlencode($whatsapp_chat_text);
        $device_detection = (wp_is_mobile())?'mobile_and_tablet':'desktop';
        $desktop_url = array(
            'web' => 'https://web.whatsapp.com/send',
            'api' => 'https://api.whatsapp.com/send',
            'app' => 'whatsapp://send',
        );
        $devices_url = array(
            'mobile_and_tablet' => 'whatsapp://send',
            'desktop' => $desktop_url[$desktop_link_type],
        );
        $chat_args = array(
            'phone' =>  $whatsapp_number,
            'text'  =>  $whatsapp_chat_text,
        );
        $chat_url = add_query_arg($chat_args, $devices_url[$device_detection]);
        ob_start('scb_html_minifier');
        ?>

        <!-- BEGIN Simple Chat Button Plugin -->
        <style>
            #simple-chat-button--container {
                position: fixed;
                bottom: <?php echo esc_attr($desktop_bottom_margin); ?>px;
                <?php echo esc_attr($button_position); ?>: 20px;
                z-index: <?php echo !empty($button_z_index)?esc_attr($button_z_index):'999999999'; ?>;
            }
            #simple-chat-button--button {
                display: block;
                position: relative;
                text-decoration: none;
                width: 60px;
                height: 60px;
                border-radius: 50%;
                -webkit-transition: all 0.2s ease-in-out;
                transition: all 0.2s ease-in-out;
                -webkit-transform: scale(1);
                transform: scale(1);
                box-shadow: 0 6px 8px 2px rgba(0, 0, 0, .15);
                background: url("data:image/svg+xml;base64,") center/44px 44px no-repeat #25D366;
            }
            #simple-chat-button--text {
                display: <?php echo esc_attr($button_text)?'block':'none'; ?>;
                position: absolute;
                width: max-content;
                background-color: #fff;
                bottom: 15px;
                <?php echo esc_attr($button_position); ?>: 70px;
                border-radius: 5px;
                padding: 5px 10px;
                color: #000;
                font-size: 13px;
                font-weight: 700;
                letter-spacing: -0.03em;
                -webkit-user-select: none;
                -moz-user-select: none;
                -ms-user-select: none;
                user-select: none;
                word-break: keep-all;
                line-height: 1em;
                text-overflow: ellipsis;
                vertical-align: middle;
                box-shadow: 0 6px 8px 2px rgba(0, 0, 0, .15);
            }
            #simple-chat-button--button:before {
                content: "";
                position: absolute;
                top: 0;
                bottom: 0;
                left: 0;
                right: 0;
                border-radius: 50%;
                -webkit-animation: scb-shockwave-animation 2s 5.3s ease-out infinite;
                animation: scb-shockwave-animation 2s 5.3s ease-out infinite;
                z-index: -1;
            }
            #simple-chat-button--button:hover {
                -webkit-transform: scale(1.06);
                transform: scale(1.06);
                -webkit-transition: all 0.2s ease-in-out;
                transition: all 0.2s ease-in-out;
            }
            @media only screen and (max-width: 1024px) {
                #simple-chat-button--container {
                    bottom: <?php echo esc_attr($tablet_bottom_margin); ?>px;
                }
            }

            @media only screen and (max-width: 768px) {
                #simple-chat-button--container {
                    bottom: <?php echo esc_attr($mobile_bottom_margin); ?>px;
                }
            }
            @-webkit-keyframes scb-shockwave-animation {
                0% {
                    -webkit-transform: scale(1);
                    transform: scale(1);
                    box-shadow: 0 0 2px rgba(0, 100, 0, .5), inset 0 0 1px rgba(0, 100, 0, .5);
                }
                95% {
                    box-shadow: 0 0 50px transparent, inset 0 0 30px transparent;
                }
                100% {
                    -webkit-transform: scale(1.2);
                    transform: scale(1.2);
                }
            }
            @keyframes scb-shockwave-animation {
                0% {
                    -webkit-transform: scale(1);
                    transform: scale(1);
                    box-shadow: 0 0 2px rgba(0, 100, 0, .5), inset 0 0 1px rgba(0, 100, 0, .5);
                }
                95% {
                    box-shadow: 0 0 50px transparent, inset 0 0 30px transparent;
                }
                100% {
                    -webkit-transform: scale(1.2);
                    transform: scale(1.2);
                }
            }
        </style>
        <div id="simple-chat-button--container">
            <a id="simple-chat-button--button" href="<?php echo esc_attr($chat_url); ?>" target="<?php echo esc_attr($button_target); ?>" rel="nofollow" aria-label="WhatsApp Chat Button" ></a>
            <span id="simple-chat-button--text"><?php echo esc_attr($button_text); ?></span>
        </div>
        <!-- END Simple Chat Button Plugin -->

        <?php
        ob_end_flush();
    }
}
add_action('wp_footer', 'scb_show_whatsapp_button');