<?php	if ( ! defined( 'ABSPATH' ) ) {		die;	}	$ttbm_post_id          = $ttbm_post_id ?? get_the_id();	$class_max_people = $class_max_people ?? '';	$max_people       = MP_Global_Function::get_post_info($ttbm_post_id, 'ttbm_travel_max_people_allow');	$tour_type  = TTBM_Function::get_tour_type( $ttbm_post_id );	if ( $max_people && $tour_type == 'general' && MP_Global_Function::get_post_info( $ttbm_post_id, 'ttbm_display_max_people', 'on' ) != 'off' ) {		?>		<div class="ttbm_list_info <?php echo esc_attr( $class_max_people ); ?>" data-placeholder>			<span class="fas fa-users"></span>			<?php echo esc_html( $max_people ); ?>			<?php esc_html_e( 'People', 'tour-booking-manager' ); ?>		</div>	<?php } ?>