namespace VitePos_Lite\Libs;

/**
 * Class Payment_Cash_Method
 *
 * @package VitePos\Libs
 */
class Payment_Cash_Method extends Payment_Method_Base {
	/**
	 * The initialize is generated by appsbd
	 *
	 * @return mixed|void
	 */
	public function initialize() {
		$this->set_admin_name( 'cash' );
		$this->set_admin_viewable( false );
		$this->title = 'Cash';
		$this->icon  = 'vps vps-payment-method';
		$this->id    = 'C';
		$this->comp  = 'basic';
		$this->add_field( 'Note', 'payment_note' );
	}

	/**
	 * The set admin settings is generated by appsbd
	 *
	 * @return mixed|void
	 */
	public function set_admin_settings() {

		$this->set_admin_title_desc(
			'Enable Cash Payment',
			'It will allows to take payment using cash.'
		);
	}
}
