namespace VitePos_Lite\Libs;

/**
 * Class Payment_Other_Method
 *
 * @package VitePos_Lite\Libs
 */
class Payment_Other_Method extends Payment_Method_Base {
	/**
	 * The initialize is generated by appsbd
	 *
	 * @return mixed|void
	 */
	public function initialize() {
		$this->set_admin_name( 'others' );
		$this->admin_data->is_enable = 'Y';
		$this->title = 'Other';
		$this->icon  = 'vps vps-star-o';
		$this->id    = 'O';
		$this->comp  = 'basic';
		$this->add_field( 'Note', 'note' );
	}

	/**
	 * The set admin settings is generated by appsbd
	 *
	 * @return mixed|void
	 */
	public function set_admin_settings() {
		$this->set_admin_title_desc(
			'Enable Other Method',
			'It will allows to take payment using others method.'
		);
	}
}
