namespace VitePos_Lite\Libs;

/**
 * Class Payment_Swipe_Method
 *
 * @package VitePos_Lite\Libs
 */
class Payment_Swipe_Method extends Payment_Method_Base {
	/**
	 * The initialize is generated by appsbd
	 *
	 * @return mixed|void
	 */
	public function initialize() {
		$this->set_admin_name( 'swipe' );
		$this->admin_data->is_enable = 'Y';
		$this->title = 'Swipe Machine';
		$this->icon  = 'vps vps-swipe-machine-2';
		$this->id    = 'S';
		$this->comp  = 'basic';
		$this->add_field( 'Last 4 Digit', 'card_info' );
	}

	/**
	 * The set admin settings is generated by appsbd
	 *
	 * @return mixed|void
	 */
	public function set_admin_settings() {
		$this->set_admin_title_desc( 'Enable Swipe Payment', 'It will allows to take payment using swipe machine.' );
	}
}
