<?php
/**
 * Its for Pos Vendor module
 *
 * @package VitePos_Lite\Modules
 */

namespace VitePos_Lite\Modules;

use Appsbd_Lite\V1\Core\BaseModule;
use Appsbd_Lite\V1\libs\Ajax_Data_Response;
use VitePos_Lite\Core\Vitepos_Module;
use VitePos_Lite\Models\Mapbd_Dashboard;
use VitePos_Lite\Models\Mapbd_POS_Dashboard;

/**
 * Class Apbd_pos_vendor
 */
class POS_Dashboard extends Vitepos_Module {
	/**
	 * The initialize is generated by appsbd
	 */
	public function initialize() {
	}
	/**
	 *  Its on init function
	 */
	public function on_init() {

		parent::on_init();
	}

	/**
	 * The OptionForm is generated by appsbd.
	 */
	public function option_form() {
		$this->set_title( 'Dashboard List' );
		$this->set_subtitle( '' );
		$this->display();
	}
	/**
	 * The get action prefix is generated by appsbd
	 *
	 * @return string
	 */
	public function get_action_prefix() {
		return 'apbd-vite-pos';
	}
	/**
	 * The data is generated by appsbd
	 */
	public function data() {
		$dashboard_data             = new \stdClass();
		$dashboard_data->order_info = Mapbd_POS_Dashboard::get_order_statistics();
		wp_send_json( $dashboard_data );
	}
}
