function wppfm_arrayObjectIndexOf(theArray,searchTerm,arrayProperty){for(var i=0,len=theArray.length;i<len;i++){if(theArray[i][arrayProperty]===searchTerm){return i}}return-1}function wppfm_getUrlParameter(key){var result="";var url=window.location.search.substring(1);var params=url.split("&");for(var i=0;i<params.length;i++){var param=params[i].split("=");if(param[0]===key){result=param[1]}}return decodeURIComponent(result.replace(/\+/g,"%20"))}function wppfm_countObjectItems(object){var count=0;for(var k in object){if(object.hasOwnProperty(k)){count++}}return count}function wppfm_isEmptyQueryObject(object){if(object===null){return true}if(object.length>0){return false}if(object.length===0){return true}for(var key in object){if(object.hasOwnProperty(key)){return false}}return true}function wppfm_incrementLast(stringWithNumber){const regex=/[0-9]+(?!.*[0-9])/;const match=stringWithNumber.match(regex);if(match){const lastNumber=parseInt(match[0],10);const incrementedNumber=lastNumber+1;return stringWithNumber.replace(regex,incrementedNumber.toString())}else{return""}}function wppfm_resetUrlForNewFeed(newFeedId,feedType){var url=new URL(location);url.searchParams.delete("feed-name");url.searchParams.set("id",newFeedId);url.searchParams.set("feed-type",feedType);history.replaceState(null,null,url)}function wppfm_storeFeedUrlInSourceData(feedUrl){jQuery("#wppfm-feed-editor-page-data-storage").data("wppfmFeedUrl",feedUrl)}